import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Users,
  UserPlus,
  Search,
  RefreshCw,
  TrendingUp,
  Brain,
  Target,
  BarChart3,
  Network,
  Zap,
  Activity,
  Lightbulb,
  TrendingDown,
  AlertCircle,
  Calendar,
  Moon,
  Sun,
  MessageCircle,
  Send,
  Bot,
  User
} from 'lucide-react';
import './App.css';

const API_BASE = 'http://localhost:3000';

function App() {
  const [contacts, setContacts] = useState([]);
  const [topClients, setTopClients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [activeTab, setActiveTab] = useState('contacts');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newContact, setNewContact] = useState({
    name: '',
    email: '',
    company: '',
    revenue: ''
  });

  // ðŸš€ NEW AI-POWERED STATE ðŸš€
  const [aiInsights, setAiInsights] = useState(null);
  const [analytics, setAnalytics] = useState(null);
  const [recommendations, setRecommendations] = useState(null);
  const [relationshipMap, setRelationshipMap] = useState(null);
  const [notifications, setNotifications] = useState([]);
  const [wsConnection, setWsConnection] = useState(null);

  // ðŸŒ™ DARK MODE STATE ðŸš€
  const [isDarkMode, setIsDarkMode] = useState(() => {
    const saved = localStorage.getItem('crm-dark-mode');
    return saved ? JSON.parse(saved) : false;
  });

  // ðŸ¤– AI CHATBOX STATE ðŸš€
  const [showChatbox, setShowChatbox] = useState(false);
  const [chatMessages, setChatMessages] = useState([
    {
      id: 1,
      type: 'ai',
      text: "ðŸ‘‹ Hi! I'm your AI CRM Assistant! Ask me anything about your contacts, analytics, or get insights!",
      timestamp: new Date()
    }
  ]);
  const [chatInput, setChatInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);

  // ðŸŒ™ DARK MODE TOGGLE FUNCTION ðŸš€
  const toggleDarkMode = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('crm-dark-mode', JSON.stringify(newMode));

    // Add smooth transition effect
    document.body.style.transition = 'background-color 0.3s ease, color 0.3s ease';
    setTimeout(() => {
      document.body.style.transition = '';
    }, 300);
  };

  // ðŸ¤– AI CHAT FUNCTIONS ðŸš€
  const handleChatSubmit = async (e) => {
    e.preventDefault();
    if (!chatInput.trim()) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      text: chatInput,
      timestamp: new Date()
    };

    setChatMessages(prev => [...prev, userMessage]);
    const currentInput = chatInput;
    setChatInput('');
    setIsTyping(true);

    try {
      // Call backend AI chat endpoint
      const response = await axios.post(`${API_BASE}/ai-chat`, {
        message: currentInput
      });

      const aiMessage = {
        id: Date.now() + 1,
        type: 'ai',
        text: response.data.response,
        timestamp: new Date()
      };
      setChatMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Error getting AI response:', error);
      const errorMessage = {
        id: Date.now() + 1,
        type: 'ai',
        text: 'Sorry, I encountered an error. Please try again!',
        timestamp: new Date()
      };
      setChatMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsTyping(false);
    }
  };


  // Fetch contacts
  const fetchContacts = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE}/contacts?limit=50`);
      setContacts(response.data.contacts);
    } catch (error) {
      console.error('Error fetching contacts:', error);
    } finally {
      setLoading(false);
    }
  };

  // Fetch top clients
  const fetchTopClients = async () => {
    try {
      const response = await axios.get(`${API_BASE}/top-clients?limit=10`);
      setTopClients(response.data.clients);
    } catch (error) {
      console.error('Error fetching top clients:', error);
    }
  };

  // ðŸš€ NEW AI-POWERED FETCH FUNCTIONS ðŸš€
  const fetchAiInsights = async () => {
    try {
      const response = await axios.get(`${API_BASE}/ai-insights`);
      setAiInsights(response.data);
    } catch (error) {
      console.error('Error fetching AI insights:', error);
    }
  };

  const fetchAnalytics = async () => {
    try {
      const response = await axios.get(`${API_BASE}/analytics`);
      setAnalytics(response.data);
    } catch (error) {
      console.error('Error fetching analytics:', error);
    }
  };

  const fetchRecommendations = async () => {
    try {
      const response = await axios.get(`${API_BASE}/recommendations`);
      setRecommendations(response.data);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };

  const fetchRelationshipMap = async () => {
    try {
      const response = await axios.get(`${API_BASE}/relationship-map`);
      setRelationshipMap(response.data);
    } catch (error) {
      console.error('Error fetching relationship map:', error);
    }
  };

  const predictRevenue = async (contactId) => {
    try {
      const response = await axios.get(`${API_BASE}/predict-revenue/${contactId}`);
      return response.data;
    } catch (error) {
      console.error('Error predicting revenue:', error);
      return null;
    }
  };

  // WebSocket connection
  const connectWebSocket = () => {
    try {
      const ws = new WebSocket('ws://localhost:3000');

      ws.onopen = () => {
        console.log('ðŸ”Œ WebSocket connected');
        setWsConnection(ws);
      };

      ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 'new_contact') {
          setNotifications(prev => [...prev, {
            id: Date.now(),
            type: 'success',
            message: `New contact added: ${data.data.name}`,
            timestamp: new Date()
          }]);
        }
      };

      ws.onclose = () => {
        console.log('ðŸ”Œ WebSocket disconnected');
        setWsConnection(null);
      };

      ws.onerror = (error) => {
        console.error('WebSocket error:', error);
        setWsConnection(null);
      };
    } catch (error) {
      console.error('Failed to connect WebSocket:', error);
    }
  };

  // Add new contact
  const handleAddContact = async (e) => {
    e.preventDefault();
    try {
      const contactData = {
        ...newContact,
        revenue: parseFloat(newContact.revenue) || 0
      };

      const response = await axios.post(`${API_BASE}/contacts`, contactData);
      setContacts(prev => [response.data, ...prev]);
      setNewContact({ name: '', email: '', company: '', revenue: '' });
      setShowAddForm(false);

      // Refresh all data
      fetchContacts();
      fetchTopClients();
      fetchAiInsights();
      fetchAnalytics();
      fetchRecommendations();
      fetchRelationshipMap();
    } catch (error) {
      console.error('Error adding contact:', error);
    }
  };

  // Filter contacts based on search
  const filteredContacts = contacts.filter(contact =>
    contact.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    contact.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    contact.company.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Format date
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  useEffect(() => {
    fetchContacts();
    fetchTopClients();
    fetchAiInsights();
    fetchAnalytics();
    fetchRecommendations();
    fetchRelationshipMap();
    connectWebSocket();

    return () => {
      if (wsConnection) {
        wsConnection.close();
      }
    };
  }, []);

  return (
    <div className={`app ${isDarkMode ? 'dark-mode' : 'light-mode'}`}>
      <header className="header">
        <div className="header-content">
          <div className="logo">
            <Users className="logo-icon" />
            <h1>CRM Assistant</h1>
          </div>
          <div className="header-actions">
            <button 
              className="btn btn-primary"
              onClick={() => setShowAddForm(true)}
            >
              <UserPlus size={20} />
              Add Contact
            </button>
            <button 
              className="btn btn-secondary"
              onClick={() => {
                fetchContacts();
                fetchTopClients();
                fetchAiInsights();
                fetchAnalytics();
                fetchRecommendations();
                fetchRelationshipMap();
              }}
            >
              <RefreshCw size={20} />
              Refresh
            </button>
            <button 
              className={`dark-mode-toggle ${isDarkMode ? 'dark' : 'light'}`}
              onClick={toggleDarkMode}
              title={isDarkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
            >
              {isDarkMode ? <Sun size={20} /> : <Moon size={20} />}
            </button>
            <button 
              className={`ai-chatbox-toggle ${showChatbox ? 'active' : ''}`}
              onClick={() => setShowChatbox(!showChatbox)}
              title="AI Assistant"
            >
              <MessageCircle size={20} />
            </button>
          </div>
        </div>
      </header>

      <main className="main">
        <nav className="tabs">
          <button 
            className={`tab ${activeTab === 'contacts' ? 'active' : ''}`}
            onClick={() => setActiveTab('contacts')}
          >
            <Users size={20} />
            All Contacts ({contacts.length})
          </button>
          <button 
            className={`tab ${activeTab === 'top-clients' ? 'active' : ''}`}
            onClick={() => setActiveTab('top-clients')}
          >
            <TrendingUp size={20} />
            Top Clients
          </button>
          <button 
            className={`tab ${activeTab === 'ai-insights' ? 'active' : ''}`}
            onClick={() => setActiveTab('ai-insights')}
          >
            <Brain size={20} />
            AI Insights
          </button>
          <button 
            className={`tab ${activeTab === 'analytics' ? 'active' : ''}`}
            onClick={() => setActiveTab('analytics')}
          >
            <BarChart3 size={20} />
            Analytics
          </button>
          <button 
            className={`tab ${activeTab === 'recommendations' ? 'active' : ''}`}
            onClick={() => setActiveTab('recommendations')}
          >
            <Target size={20} />
            Recommendations
          </button>
          <button 
            className={`tab ${activeTab === 'network' ? 'active' : ''}`}
            onClick={() => setActiveTab('network')}
          >
            <Network size={20} />
            Network Map
          </button>
        </nav>

        <div className="content">
          {activeTab === 'contacts' && (
            <div className="contacts-section">
              <div className="search-container">
                <Search className="search-icon" />
                <input
                  type="text"
                  placeholder="Search contacts..."
                  className="search-input"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>

              {loading ? (
                <div className="loading">Loading contacts...</div>
              ) : (
                <div className="contacts-grid">
                  {filteredContacts?.map(contact => (
                    <div key={contact.id} className="contact-card">
                      <div className="contact-avatar">
                        {contact.name.charAt(0).toUpperCase()}
                      </div>
                      <div className="contact-info">
                        <h3>{contact.name}</h3>
                        <p className="contact-email">{contact.email}</p>
                        <p className="contact-company">{contact.company}</p>
                        <div className="contact-meta">
                          <span className="revenue">${contact.revenue?.toLocaleString() || '0'}</span>
                          <span className="date">{formatDate(contact.created_at)}</span>
                        </div>
                        {contact.aiScore && (
                          <div className="ai-score">
                            <Brain size={14} />
                            AI Score: {contact.aiScore}/100
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {activeTab === 'top-clients' && (
            <div className="top-clients-section">
              <h2>Top Clients by Revenue</h2>
              <div className="clients-list">
                {topClients?.map((client, index) => (
                  <div key={client.id} className="client-card">
                    <div className="client-rank">#{index + 1}</div>
                    <div className="client-info">
                      <h3>{client.name}</h3>
                      <p className="client-email">{client.email}</p>
                      <p className="client-company">{client.company}</p>
                      <div className="revenue-amount">${client.revenue?.toLocaleString()}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {activeTab === 'ai-insights' && aiInsights && (
            <div className="ai-insights-section">
              <h2>AI-Powered Insights</h2>
              <div className="insights-grid">
                <div className="insight-card">
                  <div className="insight-header">
                    <Brain className="insight-icon" />
                    <h3>Contact Intelligence</h3>
                  </div>
                  <div className="insight-stats">
                    <div className="stat">
                      <span className="stat-number">{aiInsights.totalContacts}</span>
                      <span className="stat-label">Total Contacts</span>
                    </div>
                    <div className="stat">
                      <span className="stat-number">{aiInsights.averageScore}</span>
                      <span className="stat-label">Avg AI Score</span>
                    </div>
                  </div>
                </div>

                <div className="insight-card">
                  <div className="insight-header">
                    <TrendingUp className="insight-icon" />
                    <h3>Top Performers</h3>
                  </div>
                  <div className="performers-list">
                    {aiInsights.topPerformers?.slice(0, 3).map((performer, index) => (
                      <div key={performer.id} className="performer-item">
                        <div className="performer-rank">#{index + 1}</div>
                        <div className="performer-info">
                          <span className="performer-name">{performer.name}</span>
                          <span className="performer-score">Score: {performer.aiScore}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="insight-card">
                  <div className="insight-header">
                    <Target className="insight-icon" />
                    <h3>Revenue Predictions</h3>
                  </div>
                  <div className="predictions-list">
                    {aiInsights.revenuePredictions?.slice(0, 3).map(prediction => (
                      <div key={prediction.id} className="prediction-item">
                        <div className="prediction-name">{prediction.name}</div>
                        <div className="prediction-confidence">
                          <span className="current">${prediction.currentRevenue?.toLocaleString()}</span>
                          <span className="predicted">â†’ ${prediction.predictedRevenue?.toLocaleString()}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="insight-card">
                  <div className="insight-header">
                    <Activity className="insight-icon" />
                    <h3>Trends & Patterns</h3>
                  </div>
                  <div className="trends-list">
                    {aiInsights.trends?.map((trend, index) => (
                      <div key={index} className="trend-item">
                        <span className="trend-label">{trend.label}</span>
                        <span className="trend-value">{trend.value}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'analytics' && analytics && (
            <div className="analytics-section">
              <h2>Deep Analytics</h2>
              <div className="analytics-grid">
                <div className="analytics-card">
                  <h3>Overview</h3>
                  <div className="analytics-stats">
                    <div className="stat">
                      <span className="stat-value">{analytics.overview.totalContacts}</span>
                      <span className="stat-text">Total Contacts</span>
                    </div>
                    <div className="stat">
                      <span className="stat-value">${analytics.overview.totalRevenue?.toLocaleString()}</span>
                      <span className="stat-text">Total Revenue</span>
                    </div>
                    <div className="stat">
                      <span className="stat-value">${analytics.overview.averageRevenue?.toLocaleString()}</span>
                      <span className="stat-text">Average Revenue</span>
                    </div>
                  </div>
                </div>

                <div className="analytics-card">
                  <h3>Revenue Distribution</h3>
                  <div className="distribution-chart">
                    {analytics.revenueDistribution?.map((range, index) => (
                      <div key={index} className="distribution-bar">
                        <div className="range-label">{range.range}</div>
                        <div className="range-bar">
                          <div 
                            className="range-fill" 
                            style={{ width: `${(range.count / analytics.overview.totalContacts) * 100}%` }}
                          ></div>
                        </div>
                        <div className="range-count">{range.count}</div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="analytics-card">
                  <h3>Company Types</h3>
                  <div className="company-types">
                    {analytics.companyTypes?.map((type, index) => (
                      <div key={index} className="type-item">
                        <span className="type-name">{type.type}</span>
                        <span className="type-count">{type.count}</span>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="analytics-card">
                  <h3>Key Insights</h3>
                  <div className="insights-list">
                    {analytics.insights?.map((insight, index) => (
                      <div key={index} className="insight-item">
                        <Lightbulb size={16} />
                        <span>{insight}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'recommendations' && recommendations && (
            <div className="recommendations-section">
              <h2>AI Recommendations</h2>
              <div className="recommendations-grid">
                <div className="recommendation-card urgent">
                  <div className="recommendation-header">
                    <AlertCircle className="recommendation-icon" />
                    <h3>Immediate Actions</h3>
                  </div>
                  <div className="recommendations-list">
                    {recommendations.immediateActions?.map((rec, index) => (
                      <div key={index} className="recommendation-item">
                        <div className="recommendation-action">{rec.action}</div>
                        <div className="recommendation-reason">{rec.reason}</div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="recommendation-card medium">
                  <div className="recommendation-header">
                    <Calendar className="recommendation-icon" />
                    <h3>This Week</h3>
                  </div>
                  <div className="recommendations-list">
                    {recommendations.thisWeek?.map((rec, index) => (
                      <div key={index} className="recommendation-item">
                        <div className="recommendation-action">{rec.action}</div>
                        <div className="recommendation-reason">{rec.reason}</div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="recommendation-card low">
                  <div className="recommendation-header">
                    <Target className="recommendation-icon" />
                    <h3>This Month</h3>
                  </div>
                  <div className="recommendations-list">
                    {recommendations.thisMonth?.map((rec, index) => (
                      <div key={index} className="recommendation-item">
                        <div className="recommendation-action">{rec.action}</div>
                        <div className="recommendation-reason">{rec.reason}</div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="recommendation-card strategic">
                  <div className="recommendation-header">
                    <Brain className="recommendation-icon" />
                    <h3>Strategic Insights</h3>
                  </div>
                  <div className="strategic-insights">
                    {recommendations.strategicInsights.map((insight, index) => (
                      <div key={index} className="strategic-item">
                        <Lightbulb size={16} />
                        <span>{insight}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'network' && relationshipMap && (
            <div className="network-section">
              <h2>Relationship Network Map</h2>
              <div className="network-visualization">
                <div className="network-stats">
                  <div className="network-stat">
                    <span className="stat-number">{relationshipMap.totalNodes}</span>
                    <span className="stat-label">Contacts</span>
                  </div>
                  <div className="network-stat">
                    <span className="stat-number">{relationshipMap.totalConnections}</span>
                    <span className="stat-label">Connections</span>
                  </div>
                  <div className="network-stat">
                    <span className="stat-number">{relationshipMap.clusters}</span>
                    <span className="stat-label">Clusters</span>
                  </div>
                </div>

                <div className="network-nodes">
                  {relationshipMap.nodes.slice(0, 8).map(node => (
                    <div 
                      key={node.id} 
                      className={`network-node ${node.aiScore > 70 ? 'high-value' : node.aiScore > 40 ? 'medium-value' : 'low-value'}`}
                    >
                      <div className="node-name">{node.name}</div>
                      <div className="node-score">Score: {node.aiScore}</div>
                    </div>
                  ))}
                </div>

                <div className="connection-legend">
                  <div className="legend-item">
                    <div className="legend-color high-value"></div>
                    <span>High Value (70+)</span>
                  </div>
                  <div className="legend-item">
                    <div className="legend-color medium-value"></div>
                    <span>Medium Value (40-70)</span>
                  </div>
                  <div className="legend-item">
                    <div className="legend-color low-value"></div>
                    <span>Low Value (&lt;40)</span>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </main>

      {/* ðŸ¤– AI CHATBOX ðŸš€ */}
      {showChatbox && (
        <div className="ai-chatbox">
          <div className="chatbox-header">
            <div className="chatbox-title">
              <Bot size={20} />
              <span>AI Assistant</span>
            </div>
            <button 
              className="chatbox-close"
              onClick={() => setShowChatbox(false)}
            >
              Ã—
            </button>
          </div>

          <div className="chatbox-messages">
            {chatMessages.map(message => (
              <div key={message.id} className={`message ${message.type}`}>
                <div className="message-avatar">
                  {message.type === 'ai' ? <Bot size={16} /> : <User size={16} />}
                </div>
                <div className="message-content">
                  <div className="message-text">{message.text}</div>
                  <div className="message-time">
                    {message.timestamp.toLocaleTimeString()}
                  </div>
                </div>
              </div>
            ))}

            {isTyping && (
              <div className="message ai typing">
                <div className="message-avatar">
                  <Bot size={16} />
                </div>
                <div className="message-content">
                  <div className="typing-indicator">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              </div>
            )}
          </div>

          <form className="chatbox-input" onSubmit={handleChatSubmit}>
            <input
              type="text"
              placeholder="Ask me anything about your CRM..."
              value={chatInput}
              onChange={(e) => setChatInput(e.target.value)}
              className="chat-input"
            />
            <button type="submit" className="chat-send">
              <Send size={16} />
            </button>
          </form>
        </div>
      )}

      {/* Add Contact Modal */}
      {showAddForm && (
        <div className="modal-overlay" onClick={() => setShowAddForm(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>Add New Contact</h2>
              <button className="close-btn" onClick={() => setShowAddForm(false)}>
                Ã—
              </button>
            </div>
            <form onSubmit={handleAddContact} className="modal-form">
              <div className="form-group">
                <label>Name</label>
                <input
                  type="text"
                  value={newContact.name}
                  onChange={(e) => setNewContact({...newContact, name: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  value={newContact.email}
                  onChange={(e) => setNewContact({...newContact, email: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Company</label>
                <input
                  type="text"
                  value={newContact.company}
                  onChange={(e) => setNewContact({...newContact, company: e.target.value})}
                  required
                />
              </div>
              <div className="form-group">
                <label>Revenue</label>
                <input
                  type="number"
                  value={newContact.revenue}
                  onChange={(e) => setNewContact({...newContact, revenue: e.target.value})}
                  placeholder="0"
                />
              </div>
              <div className="modal-actions">
                <button type="button" onClick={() => setShowAddForm(false)}>
                  Cancel
                </button>
                <button type="submit" className="btn-primary">
                  Add Contact
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Notifications */}
      {notifications.length > 0 && (
        <div className="notifications">
          {notifications.map(notification => (
            <div key={notification.id} className="notification">
              <span>{notification.message}</span>
              <button 
                className="notification-close"
                onClick={() => setNotifications(prev => 
                  prev.filter(n => n.id !== notification.id)
                )}
              >
                Ã—
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
