import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  Users, 
  Search, 
  TrendingUp, 
  Mail, 
  Building, 
  DollarSign,
  UserPlus,
  RefreshCw,
  Brain,
  Target,
  BarChart3,
  Network,
  Zap,
  Activity,
  Lightbulb,
  TrendingDown,
  AlertCircle,
  Calendar,
  Moon,
  Sun,
  Mic,
  MicOff,
  Volume2,
  VolumeX
} from 'lucide-react';
import './App.css';

const API_BASE = 'http://localhost:3000';

function App() {
  const [contacts, setContacts] = useState([]);
  const [topClients, setTopClients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [activeTab, setActiveTab] = useState('contacts');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newContact, setNewContact] = useState({
    name: '',
    email: '',
    company: '',
    revenue: ''
  });

  // ðŸš€ NEW AI-POWERED STATE ðŸš€
  const [aiInsights, setAiInsights] = useState(null);
  const [analytics, setAnalytics] = useState(null);
  const [recommendations, setRecommendations] = useState(null);
  const [relationshipMap, setRelationshipMap] = useState(null);
  const [notifications, setNotifications] = useState([]);
  const [wsConnection, setWsConnection] = useState(null);

  // ðŸŒ™ DARK MODE STATE ðŸš€
  const [isDarkMode, setIsDarkMode] = useState(() => {
    const saved = localStorage.getItem('crm-dark-mode');
    return saved ? JSON.parse(saved) : false;
  });

  // ðŸŽ¤ VOICE COMMANDS STATE ðŸš€
  const [isListening, setIsListening] = useState(false);
  const [voiceText, setVoiceText] = useState('');
  const [speechRecognition, setSpeechRecognition] = useState(null);
  const [isMuted, setIsMuted] = useState(false);

  // ðŸŒ™ DARK MODE TOGGLE FUNCTION ðŸš€
  const toggleDarkMode = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('crm-dark-mode', JSON.stringify(newMode));

    // Add smooth transition effect
    document.body.style.transition = 'background-color 0.3s ease, color 0.3s ease';
    setTimeout(() => {
      document.body.style.transition = '';
    }, 300);
  };

  // Fetch contacts
  const fetchContacts = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE}/contacts?limit=50`);
      setContacts(response.data.contacts);
    } catch (error) {
      console.error('Error fetching contacts:', error);
    }
    setLoading(false);
  };

  // Fetch top clients
  const fetchTopClients = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE}/top-clients?limit=10`);
      setTopClients(response.data.top);
    } catch (error) {
      console.error('Error fetching top clients:', error);
    }
    setLoading(false);
  };

  // ðŸ§  AI INSIGHTS - Fetch AI-powered analytics
  const fetchAiInsights = async () => {
    try {
      const response = await axios.get(`${API_BASE}/ai-insights`);
      setAiInsights(response.data.insights);
    } catch (error) {
      console.error('Error fetching AI insights:', error);
    }
  };

  // ðŸ“Š ANALYTICS - Fetch advanced analytics
  const fetchAnalytics = async () => {
    try {
      const response = await axios.get(`${API_BASE}/analytics`);
      setAnalytics(response.data.analytics);
    } catch (error) {
      console.error('Error fetching analytics:', error);
    }
  };

  // ðŸŽ¯ RECOMMENDATIONS - Fetch AI recommendations
  const fetchRecommendations = async () => {
    try {
      const response = await axios.get(`${API_BASE}/recommendations`);
      setRecommendations(response.data.recommendations);
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    }
  };

  // ðŸ•¸ï¸ RELATIONSHIP MAP - Fetch contact connections
  const fetchRelationshipMap = async () => {
    try {
      const response = await axios.get(`${API_BASE}/relationship-map`);
      setRelationshipMap(response.data);
    } catch (error) {
      console.error('Error fetching relationship map:', error);
    }
  };

  // ðŸ”® REVENUE PREDICTION - Predict revenue for a contact
  const predictRevenue = async (contactId) => {
    try {
      const response = await axios.get(`${API_BASE}/predict-revenue/${contactId}`);
      return response.data.prediction;
    } catch (error) {
      console.error('Error predicting revenue:', error);
      return null;
    }
  };

  // ðŸ”Œ WEBSOCKET CONNECTION - Real-time updates
  const connectWebSocket = () => {
    const ws = new WebSocket(`ws://localhost:3000`);

    ws.onopen = () => {
      console.log('ðŸ”Œ Connected to real-time updates');
      setWsConnection(ws);
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log('ðŸ“¡ Real-time update:', data);

      // Add notification
      setNotifications(prev => [...prev.slice(-4), {
        id: Date.now(),
        type: data.type,
        message: data.data.message || 'New update received',
        timestamp: data.timestamp
      }]);

      // Refresh data if contact was added
      if (data.type === 'contact_added') {
        fetchContacts();
        fetchAiInsights();
        fetchAnalytics();
      }
    };

    ws.onclose = () => {
      console.log('ðŸ”Œ WebSocket disconnected');
      setWsConnection(null);
      // Reconnect after 3 seconds
      setTimeout(connectWebSocket, 3000);
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };
  };

  // Search contacts
  const searchContacts = async (term) => {
    if (!term.trim()) {
      setSearchResults([]);
      return;
    }
    try {
      const response = await axios.get(`${API_BASE}/search?q=${encodeURIComponent(term)}`);
      setSearchResults(response.data.results);
    } catch (error) {
      console.error('Error searching contacts:', error);
    }
  };

  // Add new contact
  const addContact = async (e) => {
    e.preventDefault();
    if (!newContact.name || !newContact.email) {
      alert('Name and email are required!');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post(`${API_BASE}/contacts`, {
        name: newContact.name,
        email: newContact.email,
        company: newContact.company || null,
        revenue: parseFloat(newContact.revenue) || 0
      });

      setContacts([response.data.contact, ...contacts]);
      setNewContact({ name: '', email: '', company: '', revenue: '' });
      setShowAddForm(false);
      alert('Contact added successfully!');
    } catch (error) {
      console.error('Error adding contact:', error);
      alert('Error adding contact. Please try again.');
    }
    setLoading(false);
  };

  // Load data on component mount
  useEffect(() => {
    fetchContacts();
    fetchTopClients();
    fetchAiInsights();
    fetchAnalytics();
    fetchRecommendations();
    fetchRelationshipMap();
    connectWebSocket();

    // Cleanup WebSocket on unmount
    return () => {
      if (wsConnection) {
        wsConnection.close();
      }
    };
  }, []);

  // Handle search input
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      searchContacts(searchTerm);
    }, 300);
    return () => clearTimeout(timeoutId);
  }, [searchTerm]);

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <div className={`app ${isDarkMode ? 'dark-mode' : 'light-mode'}`}>
      <header className="header">
        <div className="header-content">
          <div className="logo">
            <Users className="logo-icon" />
            <h1>CRM Assistant</h1>
          </div>
          <div className="header-actions">
            <button 
              className="btn btn-primary"
              onClick={() => setShowAddForm(true)}
            >
              <UserPlus size={20} />
              Add Contact
            </button>
            <button 
              className="btn btn-secondary"
              onClick={() => {
                fetchContacts();
                fetchTopClients();
                fetchAiInsights();
                fetchAnalytics();
                fetchRecommendations();
                fetchRelationshipMap();
              }}
            >
              <RefreshCw size={20} />
              Refresh
            </button>
            <button 
              className={`dark-mode-toggle ${isDarkMode ? 'dark' : 'light'}`}
              onClick={toggleDarkMode}
              title={isDarkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
            >
              {isDarkMode ? <Sun size={20} /> : <Moon size={20} />}
            </button>
          </div>
        </div>
      </header>

      <main className="main">
        <nav className="tabs">
          <button 
            className={`tab ${activeTab === 'contacts' ? 'active' : ''}`}
            onClick={() => setActiveTab('contacts')}
          >
            <Users size={20} />
            All Contacts ({contacts.length})
          </button>
          <button 
            className={`tab ${activeTab === 'top-clients' ? 'active' : ''}`}
            onClick={() => setActiveTab('top-clients')}
          >
            <TrendingUp size={20} />
            Top Clients
          </button>
          <button 
            className={`tab ${activeTab === 'ai-insights' ? 'active' : ''}`}
            onClick={() => setActiveTab('ai-insights')}
          >
            <Brain size={20} />
            AI Insights
          </button>
          <button 
            className={`tab ${activeTab === 'analytics' ? 'active' : ''}`}
            onClick={() => setActiveTab('analytics')}
          >
            <BarChart3 size={20} />
            Analytics
          </button>
          <button 
            className={`tab ${activeTab === 'recommendations' ? 'active' : ''}`}
            onClick={() => setActiveTab('recommendations')}
          >
            <Target size={20} />
            Recommendations
          </button>
          <button 
            className={`tab ${activeTab === 'network' ? 'active' : ''}`}
            onClick={() => setActiveTab('network')}
          >
            <Network size={20} />
            Network Map
          </button>
        </nav>

        {activeTab === 'contacts' && (
          <div className="contacts-section">
            <div className="search-bar">
              <Search className="search-icon" />
              <input
                type="text"
                placeholder="Search contacts by name or email..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
            </div>

            {loading && (
              <div className="loading">
                <RefreshCw className="spinner" />
                Loading...
              </div>
            )}

            <div className="contacts-grid">
              {(searchTerm ? searchResults : contacts).map((contact) => (
                <div key={contact.id} className="contact-card">
                  <div className="contact-header">
                    <div className="contact-avatar">
                      {contact.name.charAt(0).toUpperCase()}
                    </div>
                    <div className="contact-info">
                      <h3>{contact.name}</h3>
                      <p className="contact-email">
                        <Mail size={16} />
                        {contact.email}
                      </p>
                      {contact.company && (
                        <p className="contact-company">
                          <Building size={16} />
                          {contact.company}
                        </p>
                      )}
                    </div>
                  </div>
                  <div className="contact-footer">
                    <div className="revenue">
                      <DollarSign size={16} />
                      {formatCurrency(contact.revenue)}
                    </div>
                    <div className="date">
                      Added {formatDate(contact.created_at)}
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {searchTerm && searchResults.length === 0 && (
              <div className="no-results">
                <Search size={48} />
                <h3>No contacts found</h3>
                <p>Try a different search term</p>
              </div>
            )}
          </div>
        )}

        {activeTab === 'top-clients' && (
          <div className="top-clients-section">
            <div className="section-header">
              <h2>Top Clients by Revenue</h2>
              <p>Your highest value customers</p>
            </div>

            {loading && (
              <div className="loading">
                <RefreshCw className="spinner" />
                Loading...
              </div>
            )}

            <div className="clients-list">
              {topClients.map((client, index) => (
                <div key={client.id} className="client-card">
                  <div className="client-rank">
                    #{index + 1}
                  </div>
                  <div className="client-info">
                    <h3>{client.name}</h3>
                    <p className="client-email">
                      <Mail size={16} />
                      {client.email}
                    </p>
                    {client.company && (
                      <p className="client-company">
                        <Building size={16} />
                        {client.company}
                      </p>
                    )}
                  </div>
                  <div className="client-revenue">
                    <div className="revenue-amount">
                      {formatCurrency(client.revenue)}
                    </div>
                    <div className="revenue-label">Total Revenue</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* ðŸ§  AI INSIGHTS TAB */}
        {activeTab === 'ai-insights' && (
          <div className="ai-insights-section">
            <div className="section-header">
              <h2>ðŸ§  AI-Powered Insights</h2>
              <p>Intelligent analysis of your contact database</p>
            </div>

            {aiInsights ? (
              <div className="insights-grid">
                <div className="insight-card">
                  <div className="insight-header">
                    <Brain className="insight-icon" />
                    <h3>Contact Intelligence</h3>
                  </div>
                  <div className="insight-stats">
                    <div className="stat">
                      <span className="stat-value">{aiInsights.totalContacts}</span>
                      <span className="stat-label">Total Contacts</span>
                    </div>
                    <div className="stat">
                      <span className="stat-value">{formatCurrency(aiInsights.totalRevenue)}</span>
                      <span className="stat-label">Total Revenue</span>
                    </div>
                    <div className="stat">
                      <span className="stat-value">{formatCurrency(aiInsights.averageRevenue)}</span>
                      <span className="stat-label">Average Revenue</span>
                    </div>
                  </div>
                </div>

                <div className="insight-card">
                  <div className="insight-header">
                    <TrendingUp className="insight-icon" />
                    <h3>Top Performers</h3>
                  </div>
                  <div className="top-performers">
                    {aiInsights.contactScores.slice(0, 5).map((contact, index) => (
                      <div key={contact.contactId} className="performer-item">
                        <div className="performer-rank">#{index + 1}</div>
                        <div className="performer-info">
                          <div className="performer-name">{contact.name}</div>
                          <div className="performer-score">Score: {contact.score}/100</div>
                        </div>
                        <div className="performer-bar">
                          <div 
                            className="performer-fill" 
                            style={{ width: `${contact.score}%` }}
                          ></div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="insight-card">
                  <div className="insight-header">
                    <DollarSign className="insight-icon" />
                    <h3>Revenue Predictions</h3>
                  </div>
                  <div className="predictions">
                    {aiInsights.revenuePredictions.slice(0, 5).map((prediction, index) => (
                      <div key={prediction.contactId} className="prediction-item">
                        <div className="prediction-name">{prediction.name}</div>
                        <div className="prediction-values">
                          <span className="current">Current: {formatCurrency(prediction.currentRevenue)}</span>
                          <span className="predicted">Predicted: {formatCurrency(prediction.predictedRevenue)}</span>
                        </div>
                        <div className="prediction-confidence">
                          Confidence: {Math.round(prediction.confidence * 100)}%
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="insight-card">
                  <div className="insight-header">
                    <Activity className="insight-icon" />
                    <h3>Trends & Patterns</h3>
                  </div>
                  <div className="trends">
                    <div className="trend-item">
                      <span className="trend-label">High-Value Contacts</span>
                      <span className="trend-value">{aiInsights.trends.highValueContacts}</span>
                    </div>
                    <div className="trend-item">
                      <span className="trend-label">Tech Companies</span>
                      <span className="trend-value">{aiInsights.trends.techCompanies}</span>
                    </div>
                    <div className="trend-item">
                      <span className="trend-label">Enterprise Domains</span>
                      <span className="trend-value">{aiInsights.trends.enterpriseDomains}</span>
                    </div>
                  </div>
                </div>
              </div>
            ) : (
              <div className="loading">
                <RefreshCw className="spinner" />
                Loading AI insights...
              </div>
            )}
          </div>
        )}

        {/* ðŸ“Š ANALYTICS TAB */}
        {activeTab === 'analytics' && (
          <div className="analytics-section">
            <div className="section-header">
              <h2>ðŸ“Š Advanced Analytics</h2>
              <p>Deep dive into your contact data</p>
            </div>

            {analytics ? (
              <div className="analytics-grid">
                <div className="analytics-card">
                  <h3>ðŸ“ˆ Overview</h3>
                  <div className="overview-stats">
                    <div className="overview-stat">
                      <span className="stat-number">{analytics.overview.totalContacts}</span>
                      <span className="stat-text">Total Contacts</span>
                    </div>
                    <div className="overview-stat">
                      <span className="stat-number">{formatCurrency(analytics.overview.totalRevenue)}</span>
                      <span className="stat-text">Total Revenue</span>
                    </div>
                    <div className="overview-stat">
                      <span className="stat-number">{formatCurrency(analytics.overview.averageRevenue)}</span>
                      <span className="stat-text">Average Revenue</span>
                    </div>
                    <div className="overview-stat">
                      <span className="stat-number">{formatCurrency(analytics.overview.medianRevenue)}</span>
                      <span className="stat-text">Median Revenue</span>
                    </div>
                  </div>
                </div>

                <div className="analytics-card">
                  <h3>ðŸ’° Revenue Distribution</h3>
                  <div className="distribution-chart">
                    {Object.entries(analytics.distribution.revenueRanges).map(([range, count]) => (
                      <div key={range} className="distribution-item">
                        <span className="range-label">{range}</span>
                        <div className="range-bar">
                          <div 
                            className="range-fill" 
                            style={{ width: `${(count / analytics.overview.totalContacts) * 100}%` }}
                          ></div>
                        </div>
                        <span className="range-count">{count}</span>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="analytics-card">
                  <h3>ðŸ¢ Company Types</h3>
                  <div className="company-types">
                    {Object.entries(analytics.distribution.companyTypes).map(([type, count]) => (
                      <div key={type} className="company-type-item">
                        <span className="type-name">{type.charAt(0).toUpperCase() + type.slice(1)}</span>
                        <span className="type-count">{count}</span>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="analytics-card">
                  <h3>ðŸŽ¯ Key Insights</h3>
                  <div className="insights-list">
                    <div className="insight-item">
                      <AlertCircle size={16} />
                      <span>{analytics.insights.highValueContacts} high-value contacts (>$10K)</span>
                    </div>
                    <div className="insight-item">
                      <TrendingUp size={16} />
                      <span>{analytics.insights.potentialUpsells} potential upsell opportunities</span>
                    </div>
                    <div className="insight-item">
                      <Target size={16} />
                      <span>{analytics.insights.newOpportunities} new opportunities (no revenue)</span>
                    </div>
                    <div className="insight-item">
                      <Building size={16} />
                      <span>{analytics.insights.enterpriseClients} enterprise clients</span>
                    </div>
                  </div>
                </div>
              </div>
            ) : (
              <div className="loading">
                <RefreshCw className="spinner" />
                Loading analytics...
              </div>
            )}
          </div>
        )}

        {/* ðŸŽ¯ RECOMMENDATIONS TAB */}
        {activeTab === 'recommendations' && (
          <div className="recommendations-section">
            <div className="section-header">
              <h2>ðŸŽ¯ AI Recommendations</h2>
              <p>Smart actions to grow your business</p>
            </div>

            {recommendations ? (
              <div className="recommendations-grid">
                <div className="recommendation-card urgent">
                  <div className="recommendation-header">
                    <AlertCircle className="recommendation-icon" />
                    <h3>ðŸš¨ Immediate Actions</h3>
                  </div>
                  <div className="recommendation-list">
                    {recommendations.immediate.map((rec, index) => (
                      <div key={index} className="recommendation-item">
                        <div className="rec-priority high">{rec.priority}</div>
                        <div className="rec-content">
                          <div className="rec-action">{rec.action}</div>
                          <div className="rec-contact">Contact: {rec.contact}</div>
                          <div className="rec-reason">{rec.reason}</div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="recommendation-card weekly">
                  <div className="recommendation-header">
                    <Calendar className="recommendation-icon" />
                    <h3>ðŸ“… This Week</h3>
                  </div>
                  <div className="recommendation-list">
                    {recommendations.weekly.map((rec, index) => (
                      <div key={index} className="recommendation-item">
                        <div className="rec-priority medium">{rec.priority}</div>
                        <div className="rec-content">
                          <div className="rec-action">{rec.action}</div>
                          <div className="rec-contact">Contact: {rec.contact}</div>
                          <div className="rec-reason">{rec.reason}</div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="recommendation-card monthly">
                  <div className="recommendation-header">
                    <Target className="recommendation-icon" />
                    <h3>ðŸŽ¯ This Month</h3>
                  </div>
                  <div className="recommendation-list">
                    {recommendations.monthly.map((rec, index) => (
                      <div key={index} className="recommendation-item">
                        <div className="rec-priority low">{rec.priority}</div>
                        <div className="rec-content">
                          <div className="rec-action">{rec.action}</div>
                          <div className="rec-contact">Contact: {rec.contact}</div>
                          <div className="rec-reason">{rec.reason}</div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="recommendation-card insights">
                  <div className="recommendation-header">
                    <Lightbulb className="recommendation-icon" />
                    <h3>ðŸ’¡ Strategic Insights</h3>
                  </div>
                  <div className="insights-list">
                    {recommendations.insights.map((insight, index) => (
                      <div key={index} className={`insight-item ${insight.impact}`}>
                        <div className="insight-message">{insight.message}</div>
                        <div className="insight-impact">Impact: {insight.impact}</div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            ) : (
              <div className="loading">
                <RefreshCw className="spinner" />
                Loading recommendations...
              </div>
            )}
          </div>
        )}

        {/* ðŸ•¸ï¸ NETWORK MAP TAB */}
        {activeTab === 'network' && (
          <div className="network-section">
            <div className="section-header">
              <h2>ðŸ•¸ï¸ Relationship Network</h2>
              <p>Visualize connections between your contacts</p>
            </div>

            {relationshipMap ? (
              <div className="network-content">
                <div className="network-stats">
                  <div className="network-stat">
                    <Network size={20} />
                    <span>{relationshipMap.stats.totalNodes} Contacts</span>
                  </div>
                  <div className="network-stat">
                    <Zap size={20} />
                    <span>{relationshipMap.stats.totalConnections} Connections</span>
                  </div>
                  <div className="network-stat">
                    <Building size={20} />
                    <span>{relationshipMap.stats.companies} Companies</span>
                  </div>
                </div>

                <div className="network-visualization">
                  <div className="network-nodes">
                    {relationshipMap.nodes.map((node) => (
                      <div 
                        key={node.id} 
                        className={`network-node ${node.score > 70 ? 'high-value' : node.score > 40 ? 'medium-value' : 'low-value'}`}
                        style={{
                          left: `${Math.random() * 80 + 10}%`,
                          top: `${Math.random() * 80 + 10}%`
                        }}
                      >
                        <div className="node-name">{node.name}</div>
                        <div className="node-company">{node.company}</div>
                        <div className="node-score">Score: {node.score}</div>
                      </div>
                    ))}
                  </div>

                  <div className="network-connections">
                    {relationshipMap.connections.map((connection, index) => (
                      <div 
                        key={index} 
                        className={`connection-line ${connection.type}`}
                        style={{
                          opacity: connection.strength
                        }}
                      ></div>
                    ))}
                  </div>
                </div>

                <div className="connection-legend">
                  <div className="legend-item">
                    <div className="legend-color colleague"></div>
                    <span>Colleagues (Same Company)</span>
                  </div>
                  <div className="legend-item">
                    <div className="legend-color domain"></div>
                    <span>Domain Connections</span>
                  </div>
                </div>
              </div>
            ) : (
              <div className="loading">
                <RefreshCw className="spinner" />
                Loading network map...
              </div>
            )}
          </div>
        )}
      </main>

      {/* ðŸ”” REAL-TIME NOTIFICATIONS */}
      {notifications.length > 0 && (
        <div className="notifications-panel">
          {notifications.map((notification) => (
            <div key={notification.id} className={`notification ${notification.type}`}>
              <div className="notification-content">
                <Zap size={16} />
                <span>{notification.message}</span>
              </div>
              <button 
                className="notification-close"
                onClick={() => setNotifications(prev => prev.filter(n => n.id !== notification.id))}
              >
                Ã—
              </button>
            </div>
          ))}
        </div>
      )}

      {/* Add Contact Modal */}
      {showAddForm && (
        <div className="modal-overlay" onClick={() => setShowAddForm(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>Add New Contact</h2>
              <button 
                className="close-btn"
                onClick={() => setShowAddForm(false)}
              >
                Ã—
              </button>
            </div>
            <form onSubmit={addContact} className="contact-form">
              <div className="form-group">
                <label htmlFor="name">Name *</label>
                <input
                  type="text"
                  id="name"
                  value={newContact.name}
                  onChange={(e) => setNewContact({...newContact, name: e.target.value})}
                  required
                  placeholder="Enter full name"
                />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email *</label>
                <input
                  type="email"
                  id="email"
                  value={newContact.email}
                  onChange={(e) => setNewContact({...newContact, email: e.target.value})}
                  required
                  placeholder="Enter email address"
                />
              </div>
              <div className="form-group">
                <label htmlFor="company">Company</label>
                <input
                  type="text"
                  id="company"
                  value={newContact.company}
                  onChange={(e) => setNewContact({...newContact, company: e.target.value})}
                  placeholder="Enter company name"
                />
              </div>
              <div className="form-group">
                <label htmlFor="revenue">Revenue</label>
                <input
                  type="number"
                  id="revenue"
                  value={newContact.revenue}
                  onChange={(e) => setNewContact({...newContact, revenue: e.target.value})}
                  placeholder="Enter revenue amount"
                  min="0"
                  step="0.01"
                />
              </div>
              <div className="form-actions">
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={() => setShowAddForm(false)}
                >
                  Cancel
                </button>
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={loading}
                >
                  {loading ? 'Adding...' : 'Add Contact'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
